package hr.fer.zemris.apr.lab05.demo;import hr.fer.zemris.apr.lab05.Util;import hr.fer.zemris.apr.lab05.matrix.Matrix;import hr.fer.zemris.apr.lab05.solver.ISolver;import hr.fer.zemris.apr.lab05.solver.RungeKutta;import hr.fer.zemris.apr.lab05.solver.Trapezni;import java.io.FileNotFoundException;import java.util.List;public class Task3 {    public static void main(String[] args) throws FileNotFoundException {        ISolver trapezni = new Trapezni();        ISolver rungeKutta = new RungeKutta();        Matrix A = new Matrix(new double[][]{                {0, 1},                {-1, 0}        });        Matrix B = new Matrix(new double[][]{                {0},                {0}        });        Matrix x0 = new Matrix(new double[][]{                {0},                {1}        });        System.out.println("runge-kutta");        List<Matrix> result = rungeKutta.calculate(A, B, x0, 0.1, 30);        Util.writeResults(result, "data/task3-runge-kutta.txt");        Util.print(result, 100);        System.out.println("trapezni");        result = trapezni.calculate(A, B, x0, 0.1, 30);        Util.writeResults(result, "data/task3-trapezni.txt");        Util.print(result, 100);        A = new Matrix(new double[][]{                {0, 1},                {-1, -1}        });        System.out.println("runge-kutta-prig");        result = rungeKutta.calculate(A, B, x0, 0.1, 30);        Util.writeResults(result, "data/task3-runge-kutta-prig.txt");        Util.print(result, 100);        System.out.println("trapezni-prig");        result = trapezni.calculate(A, B, x0, 0.1, 30);        Util.writeResults(result, "data/task3-trapezni-prig.txt");        Util.print(result, 100);    }}